export function AccessibilityMenu() {
    const [isOpen, setIsOpen] = useState(false);
    const [fontSize, setFontSize] = useState(16);
    const [contrast, setContrast] = useState("normal"); // normal | high

    useEffect(() => {
        // Apply Font Size
        document.documentElement.style.fontSize = `${fontSize}px`;

        // Apply Contrast
        if (contrast === "high") {
            document.documentElement.classList.add("high-contrast");
        } else {
            document.documentElement.classList.remove("high-contrast");
        }
    }, [fontSize, contrast]);

    return (


            
                {isOpen && (
                    

Accessibility





                                Text Size


                                     setFontSize(Math.max(14, fontSize - 1))} className="w-8 h-8 p-0">
                                    {fontSize}px
                                     setFontSize(Math.min(24, fontSize + 1))} className="w-8 h-8 p-0">
                                
                            



                                Contrast
                                 setContrast(contrast === "high" ? "normal" : "high")}
                                >
                                    
                                    {contrast === "high" ? "Normal Contrast" : "High Contrast"}
                                
                            
                        
                    
                )}
            

             setIsOpen(!isOpen)}
                className="w-12 h-12 bg-zinc-900 dark:bg-white text-white dark:text-black rounded-full flex items-center justify-center shadow-lg hover:scale-110 transition-transform"
                aria-label="Accessibility Options"
            >


                    
                
            
        
    );
}

